const IVernarus1 = require('./IVernarus1.js');

const bigKey = '';

const keyPos = 5;

const enc = new IVernarus1(bigKey, keyPos);
const msg = 'Ты мне дал 100 рублей в 7:00 утра. Where is the money?!111';

console.log("orig", msg);
const encryped = enc.encryptMessage(msg);
console.log("encrypted", encryped);

const decrypted = enc.decryptMessage(encryped);
console.log("decypted:", decrypted);
